import os

def get_folder_sizes(path):
  sizes = {}
  for dirpath, dirnames, filenames in os.walk(path):
    sizes[dirpath] = 0
    for f in filenames:
      fp = os.path.join(dirpath, f)
      sizes[dirpath] += os.path.getsize(fp)
  return sizes

def print_sizes(sizes):
